import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [votesLeft, setVotesLeft] = useState(3);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/restaurants/', { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } })
      .then(response => setRestaurants(response.data))
      .catch(error => console.error('Error fetching restaurants', error));
  }, []);

  const handleVote = (restaurantId, voteType) => {
    axios.post('http://127.0.0.1:8000/api/restaurants/vote/', { restaurantId, voteType }, { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } })
      .then(response => {
        setRestaurants(response.data);
        // Update votesLeft accordingly
      })
      .catch(error => console.error('Error voting', error));
  };

  return (
    <div>
      <h1>Restaurants</h1>
      <p>Votes left today: {votesLeft}</p>
      <ul>
        {restaurants.map(restaurant => (
          <li key={restaurant.id}>
            {restaurant.name} - Votes: {/* Calculate based on user votes */}
            <button onClick={() => handleVote(restaurant.id, 'first')}>Vote 1</button>
            <button onClick={() => handleVote(restaurant.id, 'second')}>Vote 0.5</button>
            <button onClick={() => handleVote(restaurant.id, 'third')}>Vote 0.25</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RestaurantList;
