import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import './RestaurantList.css'; // Import the CSS file

const API_BASE_URL = 'http://127.0.0.1:8000/api/';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [votesLeft, setVotesLeft] = useState(0);
  const [newRestaurantName, setNewRestaurantName] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentVote, setCurrentVote] = useState({});
  const [selectedVoteType, setSelectedVoteType] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    // Check authentication status and token
    const checkAuth = () => {
      const token = localStorage.getItem('access_token');
      setIsAuthenticated(!!token);
    };

    checkAuth();
  }, []);

  useEffect(() => {
    // Fetch restaurant data
    const fetchRestaurants = async () => {
      if (!isAuthenticated) return;

      try {
        const response = await axios.get(`${API_BASE_URL}restaurants/`, { 
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } 
        });

        const { restaurants, votesLeft: newVotesLeft } = response.data;

        setRestaurants(restaurants || []);
        setVotesLeft(newVotesLeft || 3);

        // Initialize selectedVoteType with current votes if available
        const initialVoteTypes = {};
        restaurants.forEach(restaurant => {
          initialVoteTypes[restaurant.id] = currentVote[restaurant.id] || 'first'; // Use 'first' or current vote
        });
        setSelectedVoteType(initialVoteTypes);
      } catch (error) {
        console.error('Error fetching restaurants:', error);
        setError('Error fetching restaurants');
        setRestaurants([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, [isAuthenticated]);

  const handleVote = async (restaurantId) => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: '/restaurants' } });
      return;
    }

    if (votesLeft <= 0) {
      alert('No votes left for today!');
      return;
    }

    const voteType = selectedVoteType[restaurantId] || 'first'; // Default to 'first' if no type selected
    try {
      const response = await axios.post(`${API_BASE_URL}restaurants/vote/`,
        { restaurantId, voteType },
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );

      const { restaurants, votesLeft: newVotesLeft } = response.data;

      setRestaurants(restaurants || []);
      setVotesLeft(newVotesLeft || votesLeft);
      setCurrentVote(prevVotes => ({ ...prevVotes, [restaurantId]: voteType }));

      // Determine vote points
      let votePoints = "1 point";
      switch (voteType) {
        case 'first':
          votePoints = '1 point';
          break;
        case 'second':
          votePoints = '0.5 points';
          break;
        case 'third':
          votePoints = '0.25 points';
          break;
        default:
          votePoints = 'unknown';
      }

      alert(`You have cast a vote of ${votePoints}`);
    } catch (error) {
      handleApiError(error);
    }
  };

  const handleAddRestaurant = async () => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: '/restaurants' } });
      return;
    }

    if (!newRestaurantName.trim()) {
      alert('Please enter a valid restaurant name.');
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}restaurants/add_restaurant/`, 
        { name: newRestaurantName.trim() }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );
      setRestaurants(prevRestaurants => [...prevRestaurants, response.data]);
      setNewRestaurantName('');
    } catch (error) {
      console.error('Error adding restaurant:', error);
      alert('An error occurred while adding the restaurant.');
    }
  };

  const handleDeleteRestaurant = async (restaurantId) => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: `/restaurants` } });
      return;
    }

    try {
      const response = await axios.delete(`${API_BASE_URL}restaurants/${restaurantId}/delete-restaurant/`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('access_token')}`
        }
      });
      if (response.status === 204) {
        setRestaurants(prevRestaurants => prevRestaurants.filter(restaurant => restaurant.id !== restaurantId));
      } else {
        console.error('Unexpected response status:', response.status);
      }
    } catch (error) {
      console.error('Error deleting restaurant:', error);
      alert('You are not authorized to delete this restaurant.');
    }
  };

  const handleApiError = (error) => {
    console.error('API Error:', error);
    const errorMessage = error.response?.status === 403
      ? 'Daily vote limit reached. Youâ€™ve already voted 3 times today.'
      : error.response?.status === 400
      ? 'An error occurred while processing your vote.'
      : 'An error occurred while voting.';
    alert(errorMessage);
  };

  const handleVoteTypeChange = (restaurantId, voteType) => {
    setSelectedVoteType(prevVoteType => ({ ...prevVoteType, [restaurantId]: voteType }));
  };

  return (
    <div className="container">
      <h1>Restaurants</h1>
      <p>Total number of restaurants: {restaurants.length}</p>
      <p>Votes left For Today: {votesLeft}</p>
      {loading && <p className="loading">Loading, please wait...</p>}
      {error && <p className="alert">An error occurred: {error}</p>}

      {isAuthenticated ? (
        <div className="input-container">
          <h2>Add New Restaurant</h2>
          <input 
            type="text" 
            value={newRestaurantName} 
            onChange={(e) => setNewRestaurantName(e.target.value)} 
            placeholder="Restaurant Name" 
          />
          <button onClick={handleAddRestaurant}>Add Restaurant</button>
        </div>
      ) : (
        <p>Please log in to add a restaurant or vote.</p>
      )}
      <ul>
        {restaurants.length > 0 ? (
          restaurants.map((restaurant, index) => (
            <li key={restaurant.id}>
              <p>{index + 1}. {restaurant.name} - {restaurant.user.username} - Votes: {restaurant.total_votes || 0}</p>
              {isAuthenticated ? (
                <div>
                  <div className="vote-checkboxes">
                    <label>
                      <input
                        type="radio"
                        name={`vote-${restaurant.id}`}
                        value="first"
                        checked={selectedVoteType[restaurant.id] === 'first'}
                        onChange={() => handleVoteTypeChange(restaurant.id, 'first')}
                      />
                      1 point
                    </label>
                    <label>
                      <input
                        type="radio"
                        name={`vote-${restaurant.id}`}
                        value="second"
                        checked={selectedVoteType[restaurant.id] === 'second'}
                        onChange={() => handleVoteTypeChange(restaurant.id, 'second')}
                      />
                      0.5 points
                    </label>
                    <label>
                      <input
                        type="radio"
                        name={`vote-${restaurant.id}`}
                        value="third"
                        checked={selectedVoteType[restaurant.id] === 'third'}
                        onChange={() => handleVoteTypeChange(restaurant.id, 'third')}
                      />
                      0.25 points
                    </label>
                  </div>
                  <button onClick={() => handleVote(restaurant.id)}>
                    Vote
                  </button>
                  <Link to={`/edit-restaurant/${restaurant.id}/`} className="LinkButton">Edit</Link>
                  <button onClick={() => handleDeleteRestaurant(restaurant.id)}>Delete</button>
                </div>
              ) : (
                <p>Login to vote</p>
              )}
            </li>
          ))
        ) : (
          <p>No restaurants available</p>
        )}
      </ul>
    </div>
  );
};

export default RestaurantList;









