import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [votesLeft, setVotesLeft] = useState(3);
  const [newRestaurantName, setNewRestaurantName] = useState('');
  const [editRestaurantId, setEditRestaurantId] = useState(null);
  const [editRestaurantName, setEditRestaurantName] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if the user is authenticated
  useEffect(() => {
    const checkAuth = () => {
      const token = localStorage.getItem('access_token');
      if (token) {
        // Validate token with a test request or set isAuthenticated to true
        setIsAuthenticated(true); // Simplified assumption; ideally validate token
      } else {
        setIsAuthenticated(false);
      }
    };

    checkAuth();
  }, []);

  // Fetch restaurants and votes left
  useEffect(() => {
    const fetchRestaurants = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/restaurants/restaurants/', { 
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } 
        });
        console.log('API Response:', response.data);
        setRestaurants(response.data.restaurants || []);
        setVotesLeft(response.data.votesLeft || 3);
      } catch (error) {
        console.error('Error fetching restaurants:', error);
        setError('Error fetching restaurants');
        setRestaurants([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, [isAuthenticated]); // Re-fetch restaurants if authentication status changes

  const handleVote = async (restaurantId, voteType) => {
    if (!isAuthenticated) {
      alert('Please log in to vote.');
      return;
    }

    if (votesLeft <= 0) {
      alert('No votes left for today!');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/restaurants/restaurants/vote/', 
        { restaurantId, voteType }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );
      setRestaurants(response.data.restaurants || []);
      setVotesLeft(response.data.votesLeft || votesLeft);

      // Alert based on voteType
      switch (voteType) {
        case 'first':
          alert('You have cast a vote of 1 point.');
          break;
        case 'second':
          alert('You have cast a vote of 0.5 points.');
          break;
        case 'third':
          alert('You have cast a vote of 0.25 points.');
          break;
        default:
          alert('Your vote has been recorded.');
      }
    } catch (error) {
      console.error('Error voting:', error);
      if (error.response && error.response.status === 403) {
        alert('You have exceeded your vote limit for today!');
      } else {
        alert('An error occurred while voting.');
      }
    }
  };

  // Handle voting for a restaurant
  // const handleVote = async (restaurantId, voteType) => {
  //   if (!isAuthenticated) {
  //     alert('Please log in to vote.');
  //     return;
  //   }

  //   if (votesLeft <= 0) {
  //     alert('No votes left for today!');
  //     return;
  //   }

  //   try {
  //     const response = await axios.post('http://127.0.0.1:8000/api/restaurants/restaurants/vote/', 
  //       { restaurantId, voteType }, 
  //       { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
  //     );
  //     setRestaurants(response.data.restaurants || []);
  //     setVotesLeft(response.data.votesLeft || votesLeft);
  //   } catch (error) {
  //     console.error('Error voting:', error);
  //     alert('You have exceeded your vote limit for today!');
  //   }
  // };

  // Handle adding a new restaurant
  const handleAddRestaurant = async () => {
    if (!isAuthenticated) {
      alert('Please log in to add a restaurant.');
      return;
    }

    if (!newRestaurantName) {
      alert('Please enter a restaurant name.');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/restaurants/restaurants/add_restaurant/', 
        { name: newRestaurantName }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );
      setRestaurants([...restaurants, response.data]);
      setNewRestaurantName('');
    } catch (error) {
      console.error('Error adding restaurant:', error);
      alert('An error occurred while adding the restaurant.');
    }
  };

  // Handle updating a restaurant
  const handleUpdateRestaurant = async () => {
    if (!isAuthenticated) {
      alert('Please log in to update a restaurant.');
      return;
    }

    if (!editRestaurantName) {
      alert('Please enter a restaurant name.');
      return;
    }

    try {
      await axios.put(`http://127.0.0.1:8000/api/restaurants/restaurants/${editRestaurantId}/`, 
        { name: editRestaurantName }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );
      const updatedRestaurants = restaurants.map(restaurant => 
        restaurant.id === editRestaurantId ? { ...restaurant, name: editRestaurantName } : restaurant
      );
      setRestaurants(updatedRestaurants);
      setEditRestaurantId(null);
      setEditRestaurantName('');
    } catch (error) {
      console.error('Error updating restaurant:', error);
      alert('An error occurred while updating the restaurant.');
    }
  };

  // Handle deleting a restaurant
  const handleDeleteRestaurant = async (restaurantId) => {
    if (!isAuthenticated) {
      alert('Please log in to delete a restaurant.');
      return;
    }

    try {
      await axios.delete(`http://127.0.0.1:8000/api/restaurants/restaurants/${restaurantId}/`, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );
      setRestaurants(restaurants.filter(restaurant => restaurant.id !== restaurantId));
    } catch (error) {
      console.error('Error deleting restaurant:', error);
      alert('An error occurred while deleting the restaurant.');
    }
  };

  return (
    <div>
      <h1>Restaurants</h1>
      <p>Votes left today: {votesLeft}</p>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}

      {isAuthenticated ? (
        <div>
          <h2>Add New Restaurant</h2>
          <input 
            type="text" 
            value={newRestaurantName} 
            onChange={(e) => setNewRestaurantName(e.target.value)} 
            placeholder="Restaurant Name" 
          />
          <button onClick={handleAddRestaurant}>Add Restaurant</button>
        </div>
      ) : (
        <p>Please log in to add a restaurant or vote.</p>
      )}

      {isAuthenticated && editRestaurantId && (
        <div>
          <h2>Update Restaurant</h2>
          <input 
            type="text" 
            value={editRestaurantName} 
            onChange={(e) => setEditRestaurantName(e.target.value)} 
            placeholder="New Restaurant Name" 
          />
          <button onClick={handleUpdateRestaurant}>Update Restaurant</button>
        </div>
      )}

      <ul>
        {Array.isArray(restaurants) && restaurants.length > 0 ? (
          restaurants.map(restaurant => (
            <li key={restaurant.id}>
              {restaurant.name} - Votes: {restaurant.votes || 0}
              {isAuthenticated ? (
                <>
                  <button onClick={() => handleVote(restaurant.id, 'first')}>Vote 1</button>
                  <button onClick={() => handleVote(restaurant.id, 'second')}>Vote 0.5</button>
                  <button onClick={() => handleVote(restaurant.id, 'third')}>Vote 0.25</button>
                  <button onClick={() => { setEditRestaurantId(restaurant.id); setEditRestaurantName(restaurant.name); }}>Edit</button>
                  <button onClick={() => handleDeleteRestaurant(restaurant.id)}>Delete</button>
                </>
              ) : (
                <p>Login to vote</p>
              )}
            </li>
          ))
        ) : (
          <p>No restaurants available</p>
        )}
      </ul>
    </div>
  );
};

export default RestaurantList;
