import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import Link and useNavigate

const Homepage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate(); // Use useNavigate for redirection

  // Function to handle login simulation
  const handleLogin = () => {
    // Simulate authentication
    setIsAuthenticated(true);
    navigate('/'); // Redirect to homepage after login
  };

  if (!isAuthenticated) {
    return (
      <div style={containerStyle}>
        <h1 style={headingStyle}>Welcome to the Lunch Vote App</h1>
        <p style={descriptionStyle}>Please log in to access the restaurant list and see today's winner.</p>
        {/* Use a button for login */}

      </div>
    );
  }

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>Welcome to the Lunch Vote App</h1>
      <p style={descriptionStyle}>Choose a restaurant to vote for lunch today.</p>
      <p style={linkContainerStyle}>
        <Link to="/restaurants" style={linkStyle}>View Restaurants</Link>
      </p>
      <p style={linkContainerStyle}>
        <Link to="/winner" style={linkStyle}>See Today's Winner</Link>
      </p>
    </div>
  );
};

// Style for the container
const containerStyle = {
  textAlign: 'center',
  margin: '40px auto',
  padding: '20px',
  width: '80%',
  maxWidth: '600px',
  backgroundColor: '#f9f9f9',
  borderRadius: '8px',
  border: '1px solid #ddd',
};

// Style for the heading
const headingStyle = {
  fontSize: '2em',
  color: '#333',
  marginBottom: '20px',
};

// Style for the description
const descriptionStyle = {
  fontSize: '1.2em',
  color: '#555',
  marginBottom: '20px',
};

// Style for the link container
const linkContainerStyle = {
  marginBottom: '10px',
};

// Style for the links
const linkStyle = {
  fontSize: '1.1em',
  color: '#007bff',
  textDecoration: 'none',
  fontWeight: 'bold',
  transition: 'color 0.3s ease',
};

// Style for the button
const buttonStyle = {
  fontSize: '1.1em',
  color: '#fff',
  backgroundColor: '#007bff',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
  transition: 'background-color 0.3s ease',
};

export default Homepage;
