import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditRestaurant = () => {
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/api/restaurants/restaurants/${id}/`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
        });
        setName(response.data.name);
      } catch (error) {
        setError('Error fetching restaurant');
        console.error('Error fetching restaurant', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurant();
  }, [id]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.put(`http://127.0.0.1:8000/api/restaurants/restaurants/${id}/`, { name }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
      });

      console.log('Today\'s Winner Data:', response.data); // Log data here



      navigate('/'); // Redirect after successful update
    } catch (error) {
      setError('Error updating restaurant');
      console.error('Error updating restaurant', error);
    }
  };

  const containerStyle = {
    width: '80%',
    maxWidth: '600px',
    margin: '40px auto', // Adds space from the top
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
  };

  const headingStyle = {
    textAlign: 'center',
    marginBottom: '20px',
  };

  const formGroupStyle = {
    marginBottom: '20px',
  };

  const inputStyle = {
    width: '100%', // Ensure full width
    padding: '12px', // Increased padding for better touch targets
    border: '1px solid #ddd',
    borderRadius: '4px',
    fontSize: '16px',
    boxSizing: 'border-box', // Include padding and border in the element's total width and height
  };

  const buttonStyle = {
    display: 'block',
    width: '100%', // Ensure full width
    padding: '12px', // Match padding with input field
    border: 'none',
    borderRadius: '4px',
    backgroundColor: '#007bff',
    color: '#fff',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    boxSizing: 'border-box', // Include padding and border in the element's total width and height
  };

  const buttonDisabledStyle = {
    ...buttonStyle,
    backgroundColor: '#c0c0c0',
    cursor: 'not-allowed',
  };

  const errorStyle = {
    color: 'red',
    textAlign: 'center',
    marginBottom: '20px',
  };

  const loadingStyle = {
    textAlign: 'center',
    fontSize: '18px',
    color: '#007bff',
  };

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>Edit Restaurant</h1>
      {loading && <p style={loadingStyle}>Loading...</p>}
      {error && <p style={errorStyle}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div style={formGroupStyle}>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Restaurant Name"
            required
            style={inputStyle}
          />
        </div>
        <div style={formGroupStyle}>
          <button type="submit" style={loading ? buttonDisabledStyle : buttonStyle} disabled={loading}>
            Save
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditRestaurant;
