import React, { useEffect, useState } from 'react';
import axios from 'axios';

const WinnerDisplay = () => {
  const [todaysWinner, setTodaysWinner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTodaysWinner = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/restaurants/history/todays_winner/', {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
        });

        setTodaysWinner(response.data);
      } catch (err) {
        console.error('Error fetching today\'s winner', err);
        setTodaysWinner(null);
        setError('Error fetching today\'s winner');
      } finally {
        setLoading(false);
      }
    };

    fetchTodaysWinner();
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="todays-winner">
      <h1>Today's Winner</h1>
      {todaysWinner ? (
        <div className="winner-details">
          <strong>Date:</strong> {todaysWinner.date} <br />
          <strong>Winner:</strong> {todaysWinner.winner_name || 'No name available'} <br />
          <strong>Total Votes:</strong> {todaysWinner.total_votes || 'N/A'}
        </div>
      ) : (
        <div>No winner for today yet</div>
      )}
      <style jsx>{`
        .todays-winner {
          padding: 20px;
          margin: 20px;
          border: 1px solid #ddd;
          border-radius: 8px;
          background-color: #f9f9f9;
          max-width: 600px;
          margin: auto;
          text-align: center;
        }

        .winner-details {
          font-size: 18px;
          line-height: 1.6;
        }

        .loading {
          text-align: center;
          font-size: 20px;
          color: #007bff;
        }

        .error {
          text-align: center;
          font-size: 20px;
          color: red;
        }

        h1 {
          color: #333;
        }
      `}</style>
    </div>
  );
};

export default WinnerDisplay;
