import React, { useEffect, useState } from 'react';
import axios from 'axios';
// import './WinnerDisplay.css'; // Import a CSS file for styling if needed

const WinnerDisplay = () => {
  const [winners, setWinners] = useState([]);
  const [todaysWinner, setTodaysWinner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch today's winner from API
        const fetchTodaysWinner = async () => {
          try {
            const response = await axios.get('http://127.0.0.1:8000/api/restaurants/history/todays_winner/', {
              headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
            });

            console.log('Today\'s Winner Data:', response.data); // Log data here

            setTodaysWinner(response.data);
          } catch (err) {
            console.error('Error fetching today\'s winner', err);
            setTodaysWinner(null);
          }
        };

        // Fetch historical winners from API
        const fetchHistoricalWinners = async () => {
          try {
            const response = await axios.get('http://127.0.0.1:8000/api/restaurants/history/historical_winners/', {
              headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
            });

            console.log("historical_winners",response.data)

            setWinners(response.data || []);
          } catch (err) {
            console.error('Error fetching historical winners', err);
            setError('Error fetching historical winners');
            setWinners([]);
          }
        };

        // Fetch both data
        await Promise.all([fetchTodaysWinner(), fetchHistoricalWinners()]);
      } catch (err) {
        console.error('Error in fetching data', err);
        setError('An error occurred while fetching data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="winner-display">
      <h1>Winners List</h1>

      <h2>Today's Winner</h2>
      {todaysWinner ? (
        <div>
          <strong>Date:</strong> {todaysWinner.date} <br />
          <strong>Winner:</strong> {todaysWinner.winner_name || 'No name available'} <br />
          <strong>Total Votes:</strong> {todaysWinner.total_votes || 'N/A'}
        </div>
      ) : (
        <div>No winner for today yet</div>
      )}

      <h2>Historical Winners</h2>
      {winners.length > 0 ? (
        <ul>
          {winners.map((entry, index) => (
            <li key={index}>
              <strong>Date:</strong> {entry.date} <br />
              <strong>Winner:</strong> {entry.winner_name || 'No name available'} <br />
              <strong>Total Votes:</strong> {entry.total_votes || 'N/A'}
            </li>
          ))}
        </ul>
      ) : (
        <div>No historical winners yet</div>
      )}
    </div>
  );
};

export default WinnerDisplay;
