import React, { useEffect, useState } from 'react';
import axios from 'axios';

const WinnerDisplay = () => {
  const [winners, setWinners] = useState([]);
  const [todaysWinner, setTodaysWinner] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch winner history from API
    const fetchWinners = async () => {
      try {
        const historyResponse = await axios.get('http://127.0.0.1:8000/api/restaurants/history/', {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
        });
        setWinners(historyResponse.data);
      } catch (err) {
        setError('Error fetching winner history');
        console.error('Error fetching winner history', err);
      }
    };

    // Fetch today's winner from API
    const fetchTodaysWinner = async () => {
      try {
        const todaysResponse = await axios.get('http://127.0.0.1:8000/api/restaurants/history/todays_winner/', {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
        });
        setTodaysWinner(todaysResponse.data);
      } catch (err) {
        setTodaysWinner(null); // No error message for today’s winner
        console.error('Error fetching today\'s winner', err);
      }
    };

    fetchWinners();
    fetchTodaysWinner().finally(() => {
      setLoading(false);
    });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Winners List</h1>

      <h2>Today's Winner</h2>
      {todaysWinner ? (
        <div>
          <strong>Date:</strong> {todaysWinner.date} <br />
          <strong>Winner:</strong> {todaysWinner.winner ? todaysWinner.winner : 'No name available'}
        </div>
      ) : (
        <div>No winner for today yet</div>
      )}

      <h2>Historical Winners</h2>
      {winners.length > 0 ? (
        <ul>
          {winners.map((entry, index) => (
            <li key={index}>
              <strong>Date:</strong> {entry.date} <br />
              <strong>Winner:</strong> {entry.winner ? entry.winner.name : 'No name available'}
            </li>
          ))}
        </ul>
      ) : (
        <div>No historical winners yet</div>
      )}
    </div>
  );
};

export default WinnerDisplay;
