import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [votesLeft, setVotesLeft] = useState(3);

  useEffect(() => {
    // Fetch the list of restaurants
    axios.get('http://127.0.0.1:8000/api/restaurants/', { 
      headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } 
    })
      .then(response => setRestaurants(response.data))
      .catch(error => console.error('Error fetching restaurants', error));
  }, []);

  const handleVote = (restaurantId, voteType) => {
    // Ensure the voteType is valid and votesLeft are available
    if (votesLeft <= 0) {
      alert('No votes left for today!');
      return;
    }

    axios.post('http://127.0.0.1:8000/api/restaurants/vote/', 
      { restaurantId, voteType }, 
      { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
    )
      .then(response => {
        setRestaurants(response.data.restaurants); // Update according to your API response structure
        setVotesLeft(response.data.votesLeft); // Update according to your API response structure
      })
      .catch(error => {
        console.error('Error voting', error);
        alert('An error occurred while voting.');
      });
  };

  return (
    <div>
      <h1>Restaurants</h1>
      <p>Votes left today: {votesLeft}</p>
      <ul>
        {restaurants.map(restaurant => (
          <li key={restaurant.id}>
            {restaurant.name} - Votes: {restaurant.votes} {/* Make sure this matches your API response */}
            <button onClick={() => handleVote(restaurant.id, 'first')}>Vote 1</button>
            <button onClick={() => handleVote(restaurant.id, 'second')}>Vote 0.5</button>
            <button onClick={() => handleVote(restaurant.id, 'third')}>Vote 0.25</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RestaurantList;
