import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const API_BASE_URL = 'http://127.0.0.1:8000/api/restaurants/';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [votesLeft, setVotesLeft] = useState(0);
  const [newRestaurantName, setNewRestaurantName] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [votedRestaurants, setVotedRestaurants] = useState(new Set());
  const navigate = useNavigate();

  useEffect(() => {
    // Check authentication status and token
    const checkAuth = () => {
      const token = localStorage.getItem('access_token');
      setIsAuthenticated(!!token);
    };

    checkAuth();
  }, []);

  useEffect(() => {
    // Fetch restaurant data
    const fetchRestaurants = async () => {
      if (!isAuthenticated) return;

      try {
        const response = await axios.get(`${API_BASE_URL}restaurants/`, { 
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } 
        });

        console.log("API Response:", response.data);

        const { restaurants, votesLeft: newVotesLeft, votedRestaurants: newVotedRestaurants } = response.data;

        setRestaurants(restaurants || []);
        setVotesLeft(newVotesLeft || 3);
        setVotedRestaurants(new Set(newVotedRestaurants || []));
      } catch (error) {
        console.error('Error fetching restaurants:', error);
        setError('Error fetching restaurants');
        setRestaurants([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, [isAuthenticated]);

  const handleVote = async (restaurantId, voteType) => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: '/restaurants' } });
      return;
    }

    if (votesLeft <= 0) {
      alert('No votes left for today!');
      return;
    }

    if (votedRestaurants.has(voteType)) {
      alert(`You have already used your ${voteType} vote today.`);
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}restaurants/vote/`, 
        { restaurantId, voteType }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );

      const { restaurants, votesLeft: newVotesLeft, votedRestaurants: newVotedRestaurants } = response.data;

      setRestaurants(restaurants || []);
      setVotesLeft(newVotesLeft || votesLeft);
      setVotedRestaurants(new Set(newVotedRestaurants || []));

      alert(`You have cast a vote of ${voteType === 'first' ? '1 point' : voteType === 'second' ? '0.5 points' : '0.25 points'}.`);
    } catch (error) {
      handleApiError(error);
    }
  };

  const handleAddRestaurant = async () => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: '/restaurants' } });
      return;
    }

    if (!newRestaurantName.trim()) {
      alert('Please enter a valid restaurant name.');
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}restaurants/add_restaurant/`, 
        { name: newRestaurantName.trim() }, 
        { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } }
      );
      setRestaurants(prevRestaurants => [...prevRestaurants, response.data]);
      setNewRestaurantName('');
    } catch (error) {
      console.error('Error adding restaurant:', error);
      alert('An error occurred while adding the restaurant.');
    }
  };

  const handleDeleteRestaurant = async (restaurantId) => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: '/restaurants' } });
      return;
    }

    try {
      await axios.post(`${API_BASE_URL}restaurants/${restaurantId}/delete-restaurant/`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
      });
      setRestaurants(prevRestaurants => prevRestaurants.filter(restaurant => restaurant.id !== restaurantId));
    } catch (error) {
      console.error('Error deleting restaurant:', error);
      alert('An error occurred while deleting the restaurant.');
    }
  };

  const handleApiError = (error) => {
    console.error('API Error:', error);
    const errorMessage = error.response?.status === 403
      ? 'You have already voted for this restaurant today.'
      : error.response?.status === 400
      ? 'An error occurred while processing your vote.'
      : 'An error occurred while voting.';
    alert(errorMessage);
  };

  // Inline styles
  const buttonStyle = {
    padding: '10px 15px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    color: 'white',
    textDecoration: 'none',
    display: 'inline-block',
    fontSize: '14px',
    marginRight: '10px',
  };

  const primaryButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#007bff',
  };

  const primaryButtonHoverStyle = {
    ...primaryButtonStyle,
    backgroundColor: '#0056b3',
  };

  const dangerButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#dc3545',
  };

  const dangerButtonHoverStyle = {
    ...dangerButtonStyle,
    backgroundColor: '#c82333',
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Restaurants</h1>
      <p>Total number of restaurants: {restaurants.length}</p>
      <p>Votes left today: {votesLeft}</p>
      {loading && <p style={{ fontStyle: 'italic' }}>Loading, please wait...</p>}
      {error && <p style={{ color: 'red' }}>An error occurred: {error}</p>}

      {isAuthenticated ? (
        <div style={{ marginBottom: '20px' }}>
          <h2>Add New Restaurant</h2>
          <input 
            type="text" 
            value={newRestaurantName} 
            onChange={(e) => setNewRestaurantName(e.target.value)} 
            placeholder="Restaurant Name" 
            style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ddd', marginRight: '10px' }}
          />
          <button 
            onClick={handleAddRestaurant} 
            style={buttonStyle}
          >
            Add Restaurant
          </button>
        </div>
      ) : (
        <p>Please log in to add a restaurant or vote.</p>
      )}

      <ul style={{ listStyleType: 'none', padding: '0' }}>
        {restaurants.length > 0 ? (
          restaurants.map((restaurant, index) => (
            <li key={restaurant.id} style={{ marginBottom: '10px' }}>
              <p>{index + 1}. {restaurant.name} - Votes: {restaurant.total_votes || 0}</p>
              {isAuthenticated ? (
                <div>
                  <button 
                    onClick={() => handleVote(restaurant.id, 'first')} 
                    style={primaryButtonStyle} 
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = primaryButtonHoverStyle.backgroundColor}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = primaryButtonStyle.backgroundColor}
                  >
                    Vote 1
                  </button>
                  <button 
                    onClick={() => handleVote(restaurant.id, 'second')} 
                    style={primaryButtonStyle} 
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = primaryButtonHoverStyle.backgroundColor}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = primaryButtonStyle.backgroundColor}
                  >
                    Vote 0.5
                  </button>
                  <button 
                    onClick={() => handleVote(restaurant.id, 'third')} 
                    style={primaryButtonStyle} 
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = primaryButtonHoverStyle.backgroundColor}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = primaryButtonStyle.backgroundColor}
                  >
                    Vote 0.25
                  </button>
                  <Link 
                    to={`/edit-restaurant/${restaurant.id}/`} 
                    style={primaryButtonStyle} 
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = primaryButtonHoverStyle.backgroundColor}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = primaryButtonStyle.backgroundColor}
                  >
                    Edit
                  </Link>
                  <button 
                    onClick={() => handleDeleteRestaurant(restaurant.id)} 
                    style={dangerButtonStyle} 
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = dangerButtonHoverStyle.backgroundColor}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = dangerButtonStyle.backgroundColor}
                  >
                    Delete
                  </button>
                </div>
              ) : (
                <p>Login to vote</p>
              )}
            </li>
          ))
        ) : (
          <p>No restaurants available</p>
        )}
      </ul>
    </div>
  );
};

export default RestaurantList;
