import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Homepage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Simulate user authentication check (replace with real auth check)
    const checkAuth = async () => {
      // Mock async function to check authentication
      const user = await fetchUser();
      setIsAuthenticated(user !== null);
    };

    checkAuth();
  }, []);

  useEffect(() => {
    if (isAuthenticated === false) {
      navigate('/login'); // Redirect to login if not authenticated
    }
  }, [isAuthenticated, navigate]);

  if (isAuthenticated === null) {
    // Show a loading state while checking authentication
    return <div>Loading...</div>;
  }

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>Welcome to the Lunch Vote App</h1>
      <p style={descriptionStyle}>Choose a restaurant to vote for lunch today.</p>
      <p style={linkContainerStyle}>
        <a href="/restaurants" style={linkStyle}>View Restaurants</a>
      </p>
      <p style={linkContainerStyle}>
        <a href="/winner" style={linkStyle}>See Today's Winner</a>
      </p>
    </div>
  );
};

// Mock function to simulate fetching user data
const fetchUser = async () => {
  // Replace this with actual authentication logic
  return new Promise((resolve) => setTimeout(() => resolve(null), 1000)); // Simulate a 1-second delay
};

const containerStyle = {
  textAlign: 'center',
  margin: '40px auto',
  padding: '20px',
  width: '80%',
  maxWidth: '600px',
  backgroundColor: '#f9f9f9',
  borderRadius: '8px',
  border: '1px solid #ddd',
};

const headingStyle = {
  fontSize: '2em',
  color: '#333',
  marginBottom: '20px',
};

const descriptionStyle = {
  fontSize: '1.2em',
  color: '#555',
  marginBottom: '20px',
};

const linkContainerStyle = {
  marginBottom: '10px',
};

const linkStyle = {
  fontSize: '1.1em',
  color: '#007bff',
  textDecoration: 'none',
  fontWeight: 'bold',
  transition: 'color 0.3s ease',
};

export default Homepage;
