import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditRestaurant = () => {
  const [name, setName] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://127.0.0.1:8000/api/restaurants/restaurants/${id}/`, { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } })
      .then(response => setName(response.data.name))
      .catch(error => console.error('Error fetching restaurant', error));
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.put(`http://127.0.0.1:8000/api/restaurants/${id}/`, { name }, { headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` } })
      .then(() => navigate('/'))
      .catch(error => console.error('Error updating restaurant', error));
  };

  return (
    <div>
      <h1>Edit Restaurant</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditRestaurant;
