import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditRestaurant = () => {
  const [name, setName] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/api/restaurants/restaurants/${id}/`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
        });
        setName(response.data.name);
      } catch (error) {
        setError('Error fetching restaurant');
        console.error('Error fetching restaurant', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRestaurant();
  }, [id]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.put(`http://127.0.0.1:8000/api/restaurants/restaurants/${id}/`, { name }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` }
      });
      navigate('/'); // Redirect after successful update
    } catch (error) {
      setError('Error updating restaurant');
      console.error('Error updating restaurant', error);
    }
  };

  return (
    <div>
      <h1>Edit Restaurant</h1>
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Restaurant Name"
          required
        />
        <button type="submit" disabled={loading}>Save</button>
      </form>
    </div>
  );
};

export default EditRestaurant;
